// This header is hand-written, whereas most CSSProperty subclass
// headers are generated by
// core/css/properties/templates/Subclass.h.tmpl.
// CSSPropertyApplyAtRule and CSSPropertyVariable are treated as
// CSS properties in some places for convenience but are not really
// CSS properties.
#ifndef ApplyAtRule_h
#define ApplyAtRule_h

#include "core/css/properties/CSSProperty.h"
#include "platform/runtime_enabled_features.h"

namespace blink {
namespace CSSLonghand {

class ApplyAtRule : public CSSProperty {
 public:
  constexpr ApplyAtRule(CSSPropertyID id) : CSSProperty(id) {}

  bool IsEnabled() const override {
    return RuntimeEnabledFeatures::CSSApplyAtRulesEnabled();
  }
};

}  // namespace CSSLonghand
}  // namespace blink

#endif  // ApplyAtRule_h
